	var pulsadorON = false;
	var Temporizador01 = null;
	var Temporizador02 = null;

	function KM2ON()
	{
		obj = document.getElementById("ContactorKM2R");
		obj.setAttribute("transform","rotate(0 550 525)");
		obj = document.getElementById("linea2");
		obj.setAttribute("transform","translate(6.7,0)");
		obj = document.getElementById("lineaContactorKM2");
		obj.setAttribute("stroke-width","3");
	}

	function KM3OFF()
	{
		obj = document.getElementById("ContactorKM3R");
		obj.setAttribute("transform","rotate(-15 850 525)");
		obj = document.getElementById("linea3");
		obj.setAttribute("transform","translate(-6.7,0)");
		obj = document.getElementById("lineaContactorKM3");
		obj.setAttribute("stroke-width","1");
		if (!Temporizador02)
		{
			Temporizador02 = setTimeout(KM2ON, 500);
		}
	}

	function funcionEstrella()
	{
		if (pulsadorON==false){
			pulsadorON = true;
			obj = document.getElementById("PulsadorON");
			obj.setAttribute("opacity","1");
			obj = document.getElementById("PulsadorOFF");
			obj.setAttribute("opacity","0.3");
			obj = document.getElementById("ContactorKM1R");
			obj.setAttribute("transform","rotate(0 250 525)");
			obj = document.getElementById("linea1");
			obj.setAttribute("transform","translate(6.7,0)");
			obj = document.getElementById("ContactorKM3R");
			obj.setAttribute("transform","rotate(0 250 525)");
			obj = document.getElementById("linea3");
			obj.setAttribute("transform","translate(6.7,0)");
			obj = document.getElementById("rotor");
			obj.setAttribute("fill","lightgray");
			obj = document.getElementById("lineaPrincipal");
			obj.setAttribute("stroke-width","3");
			obj = document.getElementById("lineaContactorKM3");
			obj.setAttribute("stroke-width","3");
			if (!Temporizador01)
			{
				Temporizador01 = setTimeout(KM3OFF, 3000);
			}
		}
	}

	function funcionOFF()
	{
		pulsadorON = false;
		if(Temporizador01)  
		{
			clearTimeout(Temporizador01);
			Temporizador01 = null;
		}
		if(Temporizador02)  
		{
			clearTimeout(Temporizador02);
			Temporizador02 = null;
		}

		obj = document.getElementById("PulsadorON");
		obj.setAttribute("opacity","0.3");
		obj = document.getElementById("PulsadorOFF");
		obj.setAttribute("opacity","1");
		obj = document.getElementById("ContactorKM1R");
		obj.setAttribute("transform","rotate(-15 250 525)");
		obj = document.getElementById("linea1");
		obj.setAttribute("transform","translate(-6.7,0)");
		obj = document.getElementById("ContactorKM2R");
		obj.setAttribute("transform","rotate(-15 550 525)");
		obj = document.getElementById("linea2");
		obj.setAttribute("transform","translate(-6.7,0)");
		obj = document.getElementById("ContactorKM3R");
		obj.setAttribute("transform","rotate(-15 850 525)");
		obj = document.getElementById("linea3");
		obj.setAttribute("transform","translate(-6.7,0)");
		obj = document.getElementById("rotor");
		obj.setAttribute("fill","white");
		obj = document.getElementById("lineaPrincipal");
		obj.setAttribute("stroke-width","1");
		obj = document.getElementById("lineaContactorKM3");
		obj.setAttribute("stroke-width","1");
		obj = document.getElementById("lineaContactorKM2");
		obj.setAttribute("stroke-width","1");
	}



